import React, { useState } from 'react';
import './App.css';
import {Form} from 'react-bootstrap';

let marked = require('marked');

function App() {

  const [markdown,setMarkdown] = useState("");

  const setMarkdowntext = (e) => {
    setMarkdown(e.target.value)
  }


  return (

    <div className="container">
      <div className="row">
        <div className="col-md-12">
            <div className="col-md-6">
                <Form.Group controlId="formBasicMarkdown">
                  <Form.Label color="primary">MarkDown Input</Form.Label>
                  <Form.Control as="textarea" rows="40" type="text" placeholder="Enter Markdown text" value={markdown} onChange={setMarkdowntext} />
                </Form.Group>
          </div>
              <div className="col-md-6">
                <Form.Group controlId="formBasicMarkOutput">
                  <Form.Label>MarkDown Output</Form.Label>
                  <div className="container border" dangerouslySetInnerHTML={{__html : marked(markdown)}} />
                </Form.Group>

                </div>
              </div>
      </div>
</div>



  );
}

export default App;
