import React, { useState } from 'react';
import './App.css';
import {Form} from 'react-bootstrap';

let marked = require('marked');

function App() {

  const [markdown,setMarkdown] = useState("");

  const setMarkdowntext = (e) => {
    setMarkdown(e.target.value)
  }


  return (

    <div className="App container">
    <div id="markdown-container" class="col-xs-12 col-sm-10 col-sm-offset-1 col-md-8 col-md-offset-2"></div>

      <Form.Group controlId="formBasicMarkdown">
        <Form.Label>MarkDown Input</Form.Label>
        <Form.Control type="text" placeholder="Enter Markdown text" value={markdown} onChange={setMarkdowntext} />
      </Form.Group>

      <div>
        <h2>Marked Output</h2>
        <div dangerouslySetInnerHTML = {{__html : marked(markdown)}}>

        </div>
      </div>

    </div>



  );
}

export default App;
